package tutorials;

public class MaxOfThreeNos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =52 ;
		int b = 21;
		int c =125 ;
		
		if ((a>b)&&(a>c)) {
			System.out.print(a);
		}
		else if  (b>c) {
			System.out.print(b);
		}
		else {
			System.out.print(c);
		}

	}

}
