package tutorials;
import java.util.Scanner;

public class ScannerInput {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int x = sc.nextInt();
		String b = sc.next();
		int a = x + x;
		
		System.out.println(b);
		System.out.println(a);

	}

}
