package tutorials;

public class UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int x = 79;
	    int b = a++;
		int c = ++x;

		int d = --x ;
		int e = a--;
		
		System.out.println(b +" "+c);
		
		
		
		System.out.println(d +" "+e);
	}

}
